 if (new File(localSignature).exists()) {

 try (InputStream inputStream = new FileInputStream(new File(localSignature))) {

 getClient().files().uploadBuilder(dropboxPath + "/" + signature + ".jpg").uploadAndFinish(inputStream);
 }
 }

 return true;

 } catch (Exception e) {

 e.printStackTrace();

 return false;
